    ae_int_t j;
    ae_int_t i1;
    ae_int_t j1;
    ae_matrix a;
    double eps;
    ae_bool result;

    ae_frame_make(_state, &_frame_block);
    _sparsematrix_init(&s, _state, ae_true);
    ae_vector_init(&ner, 0, DT_INT, _state, ae_true);
    ae_vector_init(&x0, 0, DT_REAL, _state, ae_true);
