                state.h.ptr.pp_double[0][2] = -2;
                state.h.ptr.pp_double[1][0] = 0;
                state.h.ptr.pp_double[1][1] = 2;
                state.h.ptr.pp_double[1][2] = 0;
                state.h.ptr.pp_double[2][0] = -2;
                state.h.ptr.pp_double[2][1] = 0;
